import React, { FormEvent, useState } from "react";
import axios from 'axios'

interface Plant {
    name: string;
    location: string; 
    age: number;
    description: string;
    watered: string;
}

const HomePage: React.FC = () => {
    const [input, setInput] = useState<Plant>({
        name: '',
        location: '',
        age: 0,
        description: '',
        watered: ''
    });
    const [data, setData] = useState<any[]>([]);
    
    const handleInputChange = (e:React.ChangeEvent<HTMLInputElement>) => {
        const {name,value} = e.target;
        setInput((prevState) => ({
            ...prevState,
            [name]:value
        }))
    }
    const handleSubmit = async(e:FormEvent) => {
        e.preventDefault();
        const token = sessionStorage.getItem('token')
        try {
            const response = await axios.post('http://localhost:8080/api/input', newInput, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            })
            if (response.status === 200) {
                setData(response.data)
            }
        }
        catch(error) {
            console.log(error)
        }
    }
    const handleSave = async(item:any) => {
        try {
            const token = sessionStorage.getItem('token')
            const response = await axios.post('http://localhost:8080/api/save', item, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            })
            if (response.status === 200) {
                alert("The saved plant was successfully saved in the dashboard")
            }
        } catch (error) {
            console.log(error)
        }

    }
    return (
        <div>
            <form>
                <label>
                    <input type="text" name="name" value={input.name} onChange={handleInputChange}/> 
                </label>
            </form>
            <div>{data.length>0?(
                 <ul>
                    {data.map((item,index) => (
                        <li key={index}>{item.name}
                        <button onClick = {() => handleSave(item)}>Save</button>
                        </li>
                    ))}
                </ul>
            ): (
                <p>No data available for now</p> 
            )}</div>           
        </div>
    )
}

export default HomePage;
