import React, { FormEvent, useState } from "react";
import axios from 'axios'

interface Home {
    input: string; 
}

const HomePage: React.FC = () => {
    const [input, setInput] = useState<string>('');
    const [data, setData] = useState<any[]>([]);
    
    const handleSubmit = async(e:FormEvent) => {
        e.preventDefault();
        const token = sessionStorage.getItem('token')
        try {
            const newInput: Home = {
                input
            }
            const response = await axios.post('http://localhost:8080/api/input', newInput, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            })
            if (response.status === 200) {
                setData(response.data)
            }
        }
        catch(error) {
            console.log(error)
        }
    }
    const handleSave = async(item:any) => {
        try {
            const token = sessionStorage.getItem('token')
            const response = await axios.post('http://localhost:8080/api/save', item, {
                headers: {
                    'Authorization': `Bearer ${token}`
                }
            })
            if (response.status === 200) {
                alert("The saved plant was successfully saved in the dashboard")
            }
        } catch (error) {
            console.log(error)
        }

    }
    return (
        <div>
            <button onSubmit={handleSubmit}>Submit</button>
            <input value={input} onChange={(e) => setInput(e.target.value)} type="text" placeholder="enter"/> 
            <div>{data.length>0?(
                 <ul>
                    {data.map((item,index) => (
                        <li key={index}>{item.name}
                        <button onClick = {() => handleSave(item)}>Save</button>
                        </li>
                    ))}
                </ul>
            ): (
                <p>No data available for now</p> 
            )}</div>           
        </div>
    )
}

export default HomePage;
